#!/bin/sh
awk '{print "public static <V,T> void call" $0 "Visitor(ASTNode node, ReferenceHolder<V,T> dataholder, BiPredicate<" $0 ", ReferenceHolder<V,T>> bs) { Set<ASTNode> nodesprocessed = null; HelperVisitor<ReferenceHolder<V,T>> hv = new HelperVisitor<>(nodesprocessed, dataholder); hv.add" $0 "(bs); hv.build(node);}\n"}' <input.txt >output.txt
awk '{print "public static <V,T> void call" $0 "Visitor(ASTNode node, ReferenceHolder<V,T> dataholder, BiConsumer<" $0 ", ReferenceHolder<V,T>> bc) { Set<ASTNode> nodesprocessed = null; HelperVisitor<ReferenceHolder<V,T>> hv = new HelperVisitor<>(nodesprocessed, dataholder); hv.add" $0 "(bc); hv.build(node);}\n"}' <input.txt >> output.txt
awk '{print "public static <V,T> void call" $0 "Visitor(ASTNode node, ReferenceHolder<V,T> dataholder, BiPredicate<" $0 ", ReferenceHolder<V,T>> bs, BiConsumer<" $0 ", ReferenceHolder<V,T>> bc) { Set<ASTNode> nodesprocessed = null; HelperVisitor<ReferenceHolder<V,T>> hv = new HelperVisitor<>(nodesprocessed, dataholder); hv.add" $0 "(bs,bc); hv.build(node);}\n"}' <input.txt >> output.txt